package calculadoraTest

import spock.lang.*
import calculadora.Calculadora

class MultiplicacionSpec extends Specification {
    
    @Shared 
    Calculadora calculadora = new Calculadora()
    
    @Unroll
    def 'Se prueba el m√©todo multiplicacion, con #x * #y'() {
    	
    	expect:
            resultado == calculadora.multiplicacion(x,y)

    	where:
    		x	|   y       ||	resultado
    		-10	|   10      ||	-100
    		-10	|   -10     ||	100
    		-0.25	|   0.25    ||	-0.0625
